    def launchDialog(self):
        file_filter = "Image File (*.png *.jpg)"
        response = QtWidgets.QFileDialog.getOpenFileName(
            parent=self,
            caption="Select a file",
            directory=os.getcwd(),
            filter=file_filter,
            initialFilter="Image File (*.png *.jpg)",
        )
        dlg = CustomDialog(self)
        if response[0] != "":
            dlg.exec()

